#include "code4chief.h"void matrixToData(long **chmat, DATA *data) {	for (int i=0;i<*data->GetNTaxa();i++) {		for (int j=0;j<*data->GetNChrs();j++) {			if (chmat[i][j]!=-11 && chmat[i][j]!=-22) data->SetMatrix(i+1,j+1, pow(2,chmat[i][j]));		}	}}void typesToData(int *types, DATA *data){	for (int j=0;j<*data->GetNChrs();j++) { 		if (types[j]==0) data->SetChrType(j+1,1);		else if (types[j]==1) data->SetChrType(j+1,0);	}}